mapper.select:

	select
	    rnum, m_no, REGEXP_REPLACE(m_name, '.', '*', 2, 1) AS m_name, m_id, to_char(m_date, 'YYYY/MM/DD') as m_date, m_isdelete
	from(
	    select /*+ index_desc(member member_pk) */
	        rownum as rnum, m_no, m_name, m_id, m_date, m_isdelete
	    from member
	    where



 rownum <= #{pageNum} * #{amount}
		order by m_no desc
	) memberlist
	where m_isdelete = 'N' and rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
update member
set m_isdelete = 'Y'
where m_no = #{m_no}
