mapper.select:

	select
	    rnum, o_no, m_no, to_char(o_date, 'YYYY/MM/DD') as o_date, o_pcode, o_addr, o_daddr,
	    o_recipient, o_rectel
	from(
	    select /*+ index_desc(orders orders_pk) */
	        rownum as rnum, o_no, m_no, o_date, o_pcode, o_addr, o_daddr, o_recipient, o_rectel
	    from orders
	    where



 rownum <= #{pageNum} * #{amount}
	) orderslist
	where rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
update order_detail
set d_delivery = #{d_delivery}
where d_no = #{d_no}
