mapper.select:

	select
	    rnum, rf_no, p_no, m_no, d_no, rf_title, to_char(rf_date, 'YYYY/MM/DD') as rf_date, rf_total, rf_isrefund
	from(
	    select 
	        rownum as rnum, rf_no, p_no, m_no, d_no, rf_title, rf_date, rf_total, rf_isrefund
			from (
				select rownum as rnum, rf_no, d.p_no, o.m_no, rf.d_no, rf_title, rf_date, rf_total, rf_isrefund
			from refund rf 
			inner join order_detail d on rf.d_no = d.d_no
			inner join orders o on d.o_no = o.o_no
			order by rf_no desc )
	    where



 rownum <= #{pageNum} * #{amount}
	) refundlist 
	where rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
update order_detail
set d_refund = '환불완료'
where d_no = #{d_no}

update refund
set rf_isrefund = '처리완료'
where d_no = #{d_no}

update refund
set rf_isrefund = '환불처리중'
where d_no = #{d_no}
