mapper.select:

SELECT rv_no, m_no ,rv_tag, rv_title, to_char(rv_date,'YYYY-MM-DD') as rv_date,rv_cnt,m_name,
(select count(c_no) from c_cmnt where rv_no = rvboardlist.rv_no) as c_cnt
FROM (
	SELECT /*+ INDEX_DESC(rv_board rv_board_pk) */
			rownum as rnum, r.rv_no, m.m_no ,r.rv_tag ,r.rv_title, rv_date,r.rv_cnt,m.m_name

	FROM rv_board r inner join member m on r.m_no=m.m_no
	WHERE



rownum <= #{pageNum} * #{amount}
	) rvboardlist
WHERE rnum > (#{pageNum}-1) * #{amount}

mapper.update:
update rv_board
	set rv_title=#{rv_title}, rv_content=#{rv_content},rv_date=sysdate

		,rv_image=#{rv_image}

where rv_no=#{rv_no}
