mapper.select:

	select
	    rnum, rf_no, p_no, m_no, d_no, rf_title, to_char(rf_date, 'YYYY/MM/DD') as rf_date, rf_total
	from(
	    select /*+ index_desc(refund refund_pk) */
	        rownum as rnum, rf_no, d.p_no, o.m_no, rf.d_no, rf_title, rf_date, rf_total
			from refund rf 
			inner join order_detail d on rf.d_no = d.d_no
			inner join orders o on d.o_no = o.o_no
	    where



 rownum <= #{pageNum} * #{amount} 
	) refundlist 
	where rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
update order_detail
set d_refund = 'N'
where d_no = #{d_no}
